#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

int maxProduct(vector<int>& nums) {
    sort(nums.begin(), nums.end());
    int n = nums.size();
    return max(nums[0] * nums[1], nums[n - 1] * nums[n - 2]);
}

int main() {
    vector<int> nums = {1, 5, -10, -20, 3};
    cout << "Maximum product of two integers: " << maxProduct(nums) << endl;
    return 0;
}

